mapper.select:
SELECT (T1.discount_amount + T2.surcharge_amount) AS pay_amount, T2.bus_no, T2.prescription_no, T2.quantity, T2.medication_id AS item_id, T2.unit_code, T3.status_enum FROM adm_charge_item AS T1 LEFT JOIN med_medication_request AS T2 ON T2.bus_no = SUBSTRING(T1.bus_no, #{three}) LEFT JOIN med_medication_dispense AS T3 ON T3.med_req_id = T2.id WHERE T1.service_table = #{medMedicationRequest} AND T1.id IN
#{chargeItemId}
UNION SELECT T1.*, T2.name AS service_name FROM adm_charge_item AS T1 LEFT JOIN wor_device_request AS T4 ON T4.bus_no = SUBSTRING(T1.bus_no, #{three}) LEFT JOIN wor_device_dispense AS T5 ON T5.device_req_id = T4.id WHERE T1.service_table = #{worDeviceRequest} AND T1.id IN
#{chargeItemId}
UNION SELECT T1.*, T2.name AS service_name FROM adm_charge_item AS T1 LEFT JOIN wor_service_request AS T6 ON T6.bus_no = SUBSTRING(T1.bus_no, #{three}) WHERE T1.service_table = #{worServiceRequest} AND T1.id IN
#{chargeItemId}
