mapper.select:
SELECT T1.id AS charge_id, T1.service_table, T1.service_id, T2.bus_no, T2.prescription_no, T2.quantity, T2.medication_id AS item_id, T2.unit_code, T3.status_enum AS refund_status, T3.id AS dispense_id, T7.name AS item_name FROM adm_charge_item AS T1 LEFT JOIN med_medication_request AS T2 ON T2.bus_no = SUBSTRING(T1.bus_no, #{three}) LEFT JOIN med_medication_dispense AS T3 ON T3.med_req_id = T2.id LEFT JOIN med_medication_definition AS T7 ON T1.product_id = T7.id WHERE T1.service_table = #{medMedicationRequest} AND T1.id IN
#{chargeItemId}
UNION SELECT T1.id AS charge_id, T1.service_table, T1.service_id, T4.bus_no, T4.prescription_no, T4.quantity, T4.device_def_id AS item_id, T4.unit_code, T5.status_enum AS refund_status, T5.id AS dispense_id, T8.name AS item_name FROM adm_charge_item AS T1 LEFT JOIN wor_device_request AS T4 ON T4.bus_no = SUBSTRING(T1.bus_no, #{three}) LEFT JOIN wor_device_dispense AS T5 ON T5.device_req_id = T4.id LEFT JOIN adm_device_definition AS T8 ON T1.product_id = T8.id WHERE T1.service_table = #{worDeviceRequest} AND T1.id IN
#{chargeItemId}
UNION SELECT T1.id AS charge_id,, T1.service_table, T1.service_id, T6.bus_no, T6.prescription_no, T6.quantity, T6.activity_id AS item_id, T6.unit_code, T6.status_enum AS refund_status, null AS dispense_id, T9.name AS item_name FROM adm_charge_item AS T1 LEFT JOIN wor_service_request AS T6 ON T6.bus_no = SUBSTRING(T1.bus_no, #{three}) LEFT JOIN wor_activity_definition AS T9 ON T1.product_id = T9.id WHERE T1.service_table = #{worServiceRequest} AND T1.id IN
#{chargeItemId}
