mapper.select:
SELECT T1.item_id, T1.item_table, T1.base_quantity, T1.base_unit_code, T1.min_quantity, T1.min_unit_code, T1.lot_number, T1.price, T1.location_id, T1.location_store_id, T2.NAME AS location_name FROM wor_inventory_item AS T1 LEFT JOIN adm_location AS T2 ON T2.ID = T1.location_id AND T2.delete_flag = '0' WHERE T1.delete_flag = '0'
AND T1.location_id = #{locationId}
AND T1.item_id IN
#{itemId}
AND T1.item_table IN ( ${SqlCondition} )

SELECT T1.definition_id, T2.charge_name, T1.condition_value, T1.condition_code, T1.amount AS price FROM adm_charge_item_def_detail AS T1 LEFT JOIN adm_charge_item_definition AS T2 ON T2.ID = T1.definition_id AND T2.delete_flag = '0' WHERE T1.delete_flag = '0' AND T1.condition_code = #{conditionCode}
AND T1.definition_id IN
#{itemId}
ORDER BY T1.priority DESC

SELECT id AS definition_id, charge_name, price FROM adm_charge_item_definition WHERE delete_flag = '0'
AND id IN
#{itemId}
