mapper.select:
SELECT pt.name, pt.id_card, c.description, pt.bus_no, e.bus_no, pt.gender_enum, e.start_time, e.subject_status_enum, ogt.name, p.name, pt.phone FROM adm_encounter e LEFT JOIN adm_organization ogt ON e.organization_id = ogt.id LEFT JOIN adm_patient pt ON e.patient_id = pt.id LEFT JOIN cli_condition c ON e.patient_id = c.patient_id LEFT JOIN adm_encounter_participant ep ON ep.encounter_id = e.id LEFT JOIN adm_practitioner p ON p.id = ep.practitioner_id
ep.type_code = '1'
AND pt.id_card LIKE CONCAT('%',#{OutpatientRecordSearchParam.searchKey}, '%') OR pt.bus_no LIKE CONCAT('%',#{OutpatientRecordSearchParam.searchKey}, '%') OR e.bus_no LIKE CONCAT('%',#{OutpatientRecordSearchParam.searchKey}, '%') OR pt.name LIKE CONCAT('%',#{OutpatientRecordSearchParam.searchKey}, '%')
AND pt.phone LIKE CONCAT('%',#{phone}, '%')
AND e.start_time BETWEEN #{OutpatientRecordSearchParam.startTime} AND #{OutpatientRecordSearchParam.endTime}
AND p.name = #{OutpatientRecordSearchParam.doctorName}
ORDER BY pt.bus_no,e.bus_no,ep.practitioner_id LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM adm_encounter e LEFT JOIN adm_organization ogt ON e.organization_id = ogt.id LEFT JOIN adm_patient pt ON e.patient_id = pt.id LEFT JOIN cli_condition c ON e.patient_id = c.patient_id LEFT JOIN adm_encounter_participant ep ON ep.encounter_id = e.id LEFT JOIN adm_practitioner p ON p.id = ep.practitioner_id
ep.type_code = '1'
AND pt.id_card LIKE CONCAT('%',#{OutpatientRecordSearchParam.searchKey}, '%') OR pt.bus_no LIKE CONCAT('%',#{OutpatientRecordSearchParam.searchKey}, '%') OR e.bus_no LIKE CONCAT('%',#{OutpatientRecordSearchParam.searchKey}, '%') OR pt.name LIKE CONCAT('%',#{OutpatientRecordSearchParam.searchKey}, '%')
AND pt.phone LIKE CONCAT('%',#{phone}, '%')
AND e.start_time BETWEEN #{OutpatientRecordSearchParam.startTime} AND #{OutpatientRecordSearchParam.endTime}
AND p.name = #{OutpatientRecordSearchParam.doctorName}
